pragma solidity ^0.4.24;

// this is a test to see if bitwise ops are feasible in a merkle proof.
contract BitwiseOps {

	function readBit(byte _b) public returns (bool) {
		byte masked = _b & byte(1);
		return masked == byte(1);
	}

	function readBitAt(uint8 _b, uint8 i) public returns (bool) {
		_b = _b >> i;
		byte masked = byte(_b) & byte(1);
		return masked == byte(1);
	}

	function rightShift(uint8 _b, uint8 i) public returns (uint8) {
		return _b >> i;
	}

}
